// Making a temp dataframe called file and changing 'lable' to 'isHateful'
val file = df.withColumnRenamed("label", "isHateful")

// Register dataframe as SQL temporary view
file.createOrReplaceTempView("tweets")

// Test by querying to find the number of total hateful like tweets: 242/3416
file.groupBy("isHateful").count().show()

%sql
/* Find all hateful tweets */
select * from tweets where isHateful == 1

%sql
/* DF-visualization */
SELECT id, isHateful FROM tweets
